import logo from './logo.svg';
import './App.css';
import { useEffect, useState } from 'react';
import { convert, getAxieBriefList, getAxieBriefListTotal, getBodyParts } from './axies';
import { ProgressBar, Container, Row, Col, Accordion } from 'react-bootstrap';

function App()
{
  const [criteria, setCriteria] = useState(null);
  const [progress, setProgress] = useState(0);
  const [total, setTotal] = useState(0);


  useEffect(() =>
  {


  }, []);
  return (
    <div className="App">
      <Container>
        <Row>
          <Col sm={4}>
            <Accordion defaultActiveKey="0">
              <Accordion.Item eventKey="0">
                <Accordion.Header>Accordion Item #1</Accordion.Header>
                <Accordion.Body>
                  Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod
                  tempor incididunt ut labore et dolore magna aliqua. Ut enim ad minim
                  veniam, quis nostrud exercitation ullamco laboris nisi ut aliquip ex ea
                  commodo consequat. Duis aute irure dolor in reprehenderit in voluptate
                  velit esse cillum dolore eu fugiat nulla pariatur. Excepteur sint occaecat
                  cupidatat non proident, sunt in culpa qui officia deserunt mollit anim id
                  est laborum.
                </Accordion.Body>
              </Accordion.Item>
              <Accordion.Item eventKey="1">
                <Accordion.Header>Accordion Item #2</Accordion.Header>
                <Accordion.Body>
                  Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod
                  tempor incididunt ut labore et dolore magna aliqua. Ut enim ad minim
                  veniam, quis nostrud exercitation ullamco laboris nisi ut aliquip ex ea
                  commodo consequat. Duis aute irure dolor in reprehenderit in voluptate
                  velit esse cillum dolore eu fugiat nulla pariatur. Excepteur sint occaecat
                  cupidatat non proident, sunt in culpa qui officia deserunt mollit anim id
                  est laborum.
                </Accordion.Body>
              </Accordion.Item>
            </Accordion>
          </Col>
          <Col sm={8}>
            <div>
              <ProgressBar now={progress} max={100} label={`${(progress / total) * 100}`} />
            </div>
            <div>
              <input value={criteria} onChange={(e) => { setCriteria(e.target.value) }} ></input>
              <button onClick={async () =>
              {
                var axiesList = [], bp;
                let totalAxies = await getAxieBriefListTotal(null, 1, "PriceAsc", "Sale", JSON.parse(criteria.trim()), null);
                setTotal(Math.ceil(totalAxies / 100));
                bp = await getBodyParts();
                var i = 0;
                for (var i = 0; i < Math.ceil(totalAxies / 100); i++)
                {
                  var currentAxie = await getAxieBriefList(null, i, "PriceAsc", "Sale", JSON.parse(criteria.trim()), bp);
                  if (currentAxie == null || currentAxie == undefined || currentAxie.length == 0)
                  {
                    break;
                  }
                  axiesList = [...axiesList, ...currentAxie];
                  setProgress(i);
                }

                convert(axiesList);

              }} >export</button>
            </div>
          </Col>
        </Row>

      </Container>
    </div>

  );
}

export default App;
