import logo from './logo.svg';
import './App.css';
import { useEffect, useState } from 'react';
import { convert, getAxieBriefList, getAxieBriefListTotal, getBodyParts } from './axies';
import { ProgressBar, Container, Row, Col } from 'react-bootstrap';

function App()
{
  const [criteria, setCriteria] = useState(null);
  const [progress, setProgress] = useState(0);
  const [total, setTotal] = useState(0);


  useEffect(() =>
  {


  }, []);
  return (
    <div className="App">
      <Container>
        <Row>
          <Col sm={8}>
            <div>
            <ProgressBar now={progress} max={100}  label={`${(progress/total)*100}`} />
            </div>
            <div>
              <input value={criteria} onChange={(e) => { setCriteria(e.target.value) }} ></input>
              <button onClick={async () =>
              {
                var axiesList = [], bp;
                let totalAxies = await getAxieBriefListTotal(null, 1, "PriceAsc", "Sale", JSON.parse(criteria.trim()), null);
                setTotal(Math.ceil(totalAxies / 100));
                bp = await getBodyParts();
                var i = 0;
                for (var i = 0; i < Math.ceil(totalAxies / 100); i++)
                {
                  var currentAxie = await getAxieBriefList(null, i, "PriceAsc", "Sale", JSON.parse(criteria.trim()), bp);
                  if (currentAxie == null || currentAxie == undefined || currentAxie.length == 0)
                  {
                    break;
                  }
                  axiesList = [...axiesList, ...currentAxie];
                  setProgress(i);
                }

                convert(axiesList);

              }} >export</button>
            </div>
          </Col>
        </Row>

      </Container>
    </div>

  );
}

export default App;
